package com.basic;

public class Convert {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='A';
		int x=ch;
		System.out.println(x);
		
		int y=97;
		char ch2=(char)y;
		System.out.println(ch2);
		
		int prize=1200;
		float z=prize;
		System.out.println(z);
		
		float by=1200.89f;
		int h = (int)by;
		System.out.println(h);
		
		
		
		

	}

}
