package com.looplogic;

public class Square1to20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("the square of number from 1to 20 are:");
		for(int i=1;i<=20;i++)
		{
			int sq=i*i;
			System.out.println(i+"="+sq);
			
		}

	}

}
