package com.alphabets;

public class RightangleAlphacontinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char count='A';
		for(char i='A';i<='E';i++)
		{
			for (char j='A';j<=i;j++)
			{
				System.out.print(count+" ");
				count++;
			}
			System.out.println();
		}

	}

}
